mapper.select:
SELECT count(id) FROM article_table
WHERE title LIKE '%'||#{query}||'%' AND category = #{category}
WHERE category = #{category}
WHERE account_id = #{accountId}

SELECT c.id, c.title, c.nickname, c.view_count AS viewCount, replyCount, count(article_like_table.id) AS likeCount, c.regdate FROM (SELECT b.id, b.title, b.nickname, b.view_count, count(reply_table.id) AS replyCount, b.regdate FROM (SELECT rownum AS rnum, a.id, a.title, a.nickname, a.view_count, a.regdate FROM (SELECT article_table.id, article_table.title, account_table.nickname, article_table.view_count, article_table.regdate FROM article_table JOIN account_table ON article_table.account_id = account_table.id
WHERE article_table.title LIKE '%'||#{query}||'%' AND article_table.category = #{category}
WHERE article_table.category = #{category}
WHERE article_table.account_id = #{accountId}
)a WHERE rownum <= #{endCount})b LEFT JOIN reply_table ON b.id = reply_table.article_id WHERE b.rnum >= #{startCount} GROUP BY b.id, b.title, b.nickname,b.view_count, b.regdate)c LEFT JOIN article_like_table ON c.id = article_like_table.article_id GROUP BY c.id, c.title, c.nickname,c.view_count ,c.regdate, replyCount ORDER BY ${sort} DESC
