mapper.select:
SELECT b.id, b.accountId, b.category, b.title, b.nickname, b.viewCount, replyCount, likeCount, b.regdate FROM (SELECT rownum AS rnum, a.id, a.accountId, a.category, a.title, a.nickname, a.viewCount, replyCount, likeCount, a.regdate FROM (SELECT article_table.id, article_table.account_id AS accountId, article_table.category, article_table.title, account_table.nickname, article_table.view_count AS viewCount, count(reply_table.id) AS replyCount, count(article_like_table.id) AS likeCount, article_table.regdate FROM article_table JOIN account_table ON article_table.account_id = account_table.id LEFT JOIN reply_table ON article_table.id = reply_table.article_id LEFT JOIN article_like_table ON article_table.id = article_like_table.article_id
WHERE article_table.title LIKE '%'||#{query}||'%' AND article_table.category = LOWER(#{category}) AND article_table.importance = 0
WHERE article_table.account_id = #{accountId}
WHERE article_table.category = LOWER(#{category})
GROUP BY article_table.id, article_table.account_id, article_table.category, article_table.title, account_table.nickname,article_table.view_count, article_table.regdate ORDER BY ${sort} DESC )a WHERE rownum <= #{endCount} )b WHERE b.rnum >= #{startCount}
